mapper.insert:
insert into comment(re_num, re_lev, re_step, content, user_id) 
select #{re_num}
,ifnull((select max(ifnull(re_lev,0))+1 from comment where re_num = #{re_num}),1)
, 1 
, #{content}
, #{user_id}

insert into comment(re_num, re_lev, re_step, content, user_id) 
select #{re_num}
, ifnull(#{re_lev},0)+1
, ifnull(#{re_step},0)+1
, #{content}
, #{user_id}
